$(function() {

    var getId = $('input[name="id"]'); 
    getFakultas();
    if ( getId.val().length > 0 ) {

        $('.active-useraccess').hide();
        getFakultas( getId.val() );
        
        getProdi( $('input[name="data_fakultas"]').val(), $('input[name="data_prodi"]').val() );
    }

    


    $('#content-useraccess').on('change', 'input[name="level"]', function() {

        viewStatusAccess( $(this).val() );
    });


    $('#content-useraccess').on('keyup', 'input[name="username"]', function() {

        $('#username').html( "(" + $(this).val() + ")" );
    });

    // show data panel or form
    $('.active-useraccess').on('click', function() {

        $('#btn-level-access').hide();
        viewContentUserAccess();
        $('html,body').animate({scrollTop: document.body.scrollHeight}, 1500);
    });


    // close panel 
    $('#content-useraccess').on('click', '.close-panel', function() {

        $('#btn-level-access').fadeIn();
        $('html,body').animate({scrollTop: 0}, 1500);
        $('#content-useraccess').fadeOut(1500, function() {

            $(this).html("");
        });
    });


    // fakultas on change
    $('#element-fakultas').on('change', '#fakultas', function(){

        getProdi( $(this).val() );
    });





    $('.data-form').on('submit', function( e ) {

        var formData = $(this).serialize();
        $.ajax({

            type : "GET",
            url  : serverside + 'csrf',
            dataType : "json",
            success : function( csrf ) {

                var name = csrf.name;
                var hash = csrf.hash;

                $.ajax({

                    type: "POST",
                    url : serverside + 'account/processInsertStudent',
                    data: formData + '&' + name + '=' + hash,
                    dataType : "json",
                    beforeSend: function() {

                        $('#process-loader').modal('show');
                    }, 
                    success : function ( response ) {

                        if ( response ){

                            window.location.href = serverside + 'account?p=students';
                        } else {

                            $('#process-loader').modal('hide');
                            notification("dark", "Pengoperasian gagal coba menggunakan username atau nomor identitas yang lain");
                        }
                    }
                });
            }
        });

        e.preventDefault();
    });


    // update
    $('.data-form-update').on('submit', function( e ) {

        var formData = $(this).serialize();
        $.ajax({

            type : "GET",
            url  : serverside + 'csrf',
            dataType : "json",
            success : function( csrf ) {

                var name = csrf.name;
                var hash = csrf.hash;

                $.ajax({

                    type: "POST",
                    url : serverside + 'account/processUpdateStudent',
                    data: formData + '&' + name + '=' + hash,
                    dataType : "json",
                    beforeSend: function() {

                        $('#process-loader').modal('show');
                    }, 
                    success : function ( response ) {

                        if ( response ){

                            window.location.href = serverside + 'account?p=students';
                        } else {

                            $('#process-loader').modal('hide');
                            notification("dark", "Pengoperasian gagal coba cek kembali");
                        }
                    }
                });
            }
        });

        e.preventDefault();
    });
});











