// data fakultas
function getFakultas( id = null ) {

    var valueFaculty = $('input[name="data_fakultas"]').val();
    
    $.ajax({

        type : "GET",
        url  : serverside + 'student_department/getDataFakultas',
        data : 'id=' + id,
        dataType : "json",
        success : function( response ) {

            var elementHTML = '';
            if ( response.status ) {

                response.data.forEach( function( item ) {

                    if ( item.status == "active" ) {

                        let selected = "";
                        if ( id ) {
                            
                            if ( item.id_faculty == valueFaculty ) {

                                selected = 'selected="selected"';
                            }
                        }

                        elementHTML += '<option value="'+ item.id_faculty +'"' + selected +'>'+ item.name + '</option>';
                    }
                } );
            }

            $('#element-fakultas').html( '<select name="fakultas" data-placeholder="Pilih fakultas . . ." id="fakultas"><option value="">-- Fakultas --</option>' + elementHTML + '</select>' );
            $('#fakultas').chosen({width:'100%'});
        }
    });
}


function getProdi( getId, select = null ) {

    $.ajax({

        type : "GET",
        url  : serverside + 'student_department/getDataProdiByFakultas',
        data : "id_faculty=" + getId,
        dataType : "json",
        success : function( response ) {

            var elementHTML = '';
            if ( response.data[0].status_prodi ) {

                response.data[0].dataProdi.forEach( function( item ) {

                    if ( item.status == "active" ) {

                        let selected = "";
                        if ( select ) {

                            if ( item.id_prodi == select ) selected = "selected";
                        }

                        elementHTML += '<option value="'+ item.id_prodi +'" selected="'+ selected +'">'+ item.name +'</option>';
                    }
                } );
            }

            $('#element-prodi').html( '<select name="prodi" data-placeholder="Pilih prodi . . ." id="prodi"><option value="">-- Prodi --</option>' + elementHTML + '</select>' );
            $('#prodi').chosen({width:'100%'});
        }
    });
}

