function notification( type, html, position = 'bottom-left' ) {

    $.niftyNoty({
        type: type,
        container : 'floating',
        html : html,
        closeBtn : false,
        timer : 6000,
        floating: {
            position: position,
            animationIn: 'jellyIn',
            animationOut: 'jellyOut'
        },
    });
}